import { GET_ALL_BREEDS, GET_BREED_DETAILS, CREATE_BREED, DELETE_BREED, SET_CURRENT_PAGE, SEARCH_BREED, ORDER_FILTER, ORDER_WEIGHT_FILTER, CREATION_FILTER } from "../actions/index.js";

const initialState = {
    breeds: [],
    temperaments: [],
    breedDetail: {},
    page: 1,
}


function rootReducer(state = initialState, action) {
    switch(action.type){
        case GET_ALL_BREEDS:
            return {...state, breeds: action.payload}
        
        case GET_BREED_DETAILS:
            return {...state, breedDetail: action.payload}
        
        case SET_CURRENT_PAGE:
            return {...state, page: action.payload}

        case SEARCH_BREED: 
            return {...state, breeds: action.payload}

        case ORDER_FILTER:
            let sorted = [...state.breeds];

            sorted = sorted.sort((a , b) => {
                if(a.name > b.name) return action.payload === "A - Z" ? 1 : -1;
                if(a.name < b.name) return action.payload === "Z - A" ? 1 : -1;
                
            })
            return {...state, breeds: sorted}
        
        case ORDER_WEIGHT_FILTER:
            let weight_sorted = [...state.breeds];

            weight_sorted = weight_sorted.sort((a , b) => {
                if(a.weight_max > b.weight_max) return action.payload === "Less" ? 1 : -1;
                if(a.weight_max < b.weight_max) return action.payload === "More" ? 1 : -1;
                
            })
            return {...state, breeds: weight_sorted}

        case CREATION_FILTER:
            let allBreeds = [...state.breeds];
            let creation_filter;
            console.log(allBreeds);
            console.log(state.breeds);
            console.log(action.payload);
            if (action.payload === "Exist") creation_filter = allBreeds?.filter(c => c.id.length > 35 );
            creation_filter =  action.payload === "Created" && allBreeds?.filter(c => c.id.length === 36 );
            console.log(creation_filter);
            console.log(state.breeds[0].id);
            // creation_filter = action.payload === "Created" && creation_filter.filter(c => c.id.length === 36 );
            // creation_filter = action.payload === "Existing" && creation_filter.filter(c => c.id.length < 36 );

            return {... state, breeds: creation_filter}

        default: return { ...state }
    }
}

export default rootReducer;
