import axios from 'axios';

export const GET_ALL_BREEDS = "GET_ALL_BREEDS";
export const GET_BREED_DETAILS = "GET_BREED_DETAILS";
export const CREATE_BREED = "CREATE_BREED";
export const DELETE_BREED = "DELETE_BREED";



export const getAllBreeds = () => async dispatch => {
    try {
        const breeds = await axios.get('http://localhost:3001/dogs');
        return dispatch({
            type: GET_ALL_BREEDS,
            payload: breeds
        })
    } catch (error) {
        return dispatch({
            type: GET_ALL_BREEDS,
            payload: error
        })
    }

}

export const getBreedDetails = (id) => async dispatch => {
    try {
        const breed = await axios.get(`http://localhost:3001/dogs/${id}`);
        return dispatch({
            type: GET_BREED_DETAILS,
            payload: breed
        })
    } catch (error) {
        return dispatch({
            type: GET_BREED_DETAILS,
            payload: error
        })
    }
}
