import React, { useEffect, useState }  from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllTemperaments } from "../../actions";



export default function CreateBreed() {

    const dispatch = useDispatch();
    const temperaments = useSelector(state => state.temperaments)

    const [breed, setBreed] = useState({
        name: "",
        height_min: 0,
        height_max: 0,
        weight_min: 0,
        weight_max: 0,
        life_span: 0,
        img: "",
        temperament: []
    })

    useEffect(() => {
        dispatch(getAllTemperaments())
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    const handleChange = (e) => {
        setBreed({
            ...breed,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div>
            <h3>Create your Breed</h3>
            <form>
                
                <label>Enter name:
                    <input 
                        onChange={(e) => handleChange(e)}
                        type="text"
                        name="name"
                        value={breed.name}
                        placeholder="Enter name..."
                    />
                </label>
                
                <label>Height Min:
                    <input
                        onChange={(e) => handleChange(e)}
                        type="number"
                        name="height_min"
                        value={breed.height_min}
                        placeholder="1 - 100..."
                    />
                </label>
                
                <label>Height Max:
                    <input 
                        onChange={(e) => handleChange(e)}
                        type="number"
                        name="height_max"
                        value={breed.height_max}
                        placeholder="1 - 100..."
                    />
                </label>

                <label>Weight Min:
                    <input
                        onChange={(e) => handleChange(e)}
                        type="number"
                        name="weight_min"
                        value={breed.weight_min}
                        placeholder="1 - 100..."
                    />
                </label>

                <label>Weight Max:
                    <input
                        onChange={(e) => handleChange(e)}
                        type="number"
                        name="weight_max"
                        value={breed.weight_max}
                        placeholder="1 - 100..."
                    />
                </label>

                <label>Life span:
                    <input
                        onChange={(e) => handleChange(e)}
                        type="number"
                        name="life_span"
                        value={breed.life_span}
                        placeholder="1 - 25..."
                    />
                </label>
                
                <label>Image:
                    <input 
                        onChange={(e) => handleChange(e)}
                        type="text"
                        name="img"
                        value={breed.img}
                        placeholder="URL..."
                    />
                </label>

                <div>
                    <select>
                        <option>Temperaments</option>
                        {temperaments?.map(t => {
                            return (
                                <option>{t.name}</option>
                            )
                        })}
                    </select>
                </div>

                <button type="submit">Create</button>
            </form>
        </div>
    )



}
