import { GET_ALL_BREEDS, GET_BREED_DETAILS, CREATE_BREED, DELETE_BREED, SET_CURRENT_PAGE, SEARCH_BREED, ORDER_FILTER, ORDER_WEIGHT_FILTER } from "../actions/index.js";

const initialState = {
    breeds: [],
    temperaments: [],
    breedDetail: {},
    page: 1,
    search: []
}


function rootReducer(state = initialState, action) {
    switch(action.type){
        case GET_ALL_BREEDS:
            return {...state, breeds: action.payload}
        
        case GET_BREED_DETAILS:
            return {...state, breedDetail: action.payload}
        
        case SET_CURRENT_PAGE:
            return {...state, page: action.payload}

        case SEARCH_BREED: 
            return {...state, breeds: action.payload}

        case ORDER_FILTER:
            let sorted = [...state.breeds];

            sorted = sorted.sort((a , b) => {
                if(a.name > b.name) return action.payload === "A - Z" ? 1 : -1;
                if(a.name < b.name) return action.payload === "Z - A" ? 1 : -1;
                
            })
            return {...state, breeds: sorted}
        
        case ORDER_WEIGHT_FILTER:
            let weight_sorted = [...state.breeds];

            weight_sorted = weight_sorted.sort((a , b) => {
                if(a.weight_min > b.weight_min) return action.payload === "Less" ? 1 : -1;
                if(a.weight_min < b.weight_min) return action.payload === "More" ? 1 : -1;
                
            })
            return {...state, breeds: weight_sorted}

        default: return { ...state }
    }
}

export default rootReducer;
